// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

interface ICompliance {
    /**
     *  this event is emitted when a token has been bound to the compliance contract
     *  the event is emitted by the bindToken function
     *  `_token` is the address of the token to bind
     */
    event TokenBound(address _token);

    /**
     *  this event is emitted when a token has been unbound from the compliance contract
     *  the event is emitted by the unbindToken function
     *  `_token` is the address of the token to unbind
     */
    event TokenUnbound(address _token);

    /**
     *  @dev binds a token to the compliance contract
     *  @param _token address of the token to bind
     *  Emits a TokenBound event
     */
    function bindToken(address _token) external;

    /**
     *  @dev unbinds a token from the compliance contract
     *  @param _token address of the token to unbind
     *  Emits a TokenUnbound event
     */
    function unbindToken(address _token) external;

    /**
     *  @dev function called whenever tokens are transferred
     *  from one wallet to another
     *  this function can update state variables in the compliance contract
     *  these state variables being used by `canTransfer` to decide if a transfer
     *  is compliant or not depending on the values stored in these state variables and on
     *  the parameters of the compliance smart contract
     *  @param _from The address of the sender
     *  @param _to The address of the receiver
     *  @param _amount The amount of tokens involved in the transfer
     */
    function transferred(address _from, address _to, uint256 _amount) external;

    /**
     *  @dev function called whenever tokens are created
     *  on a wallet
     *  this function can update state variables in the compliance contract
     *  these state variables being used by `canTransfer` to decide if a transfer
     *  is compliant or not depending on the values stored in these state variables and on
     *  the parameters of the compliance smart contract
     *  @param _to The address of the receiver
     *  @param _amount The amount of tokens involved in the transfer
     */
    function created(address _to, uint256 _amount) external;

    /**
     *  @dev function called whenever tokens are destroyed
     *  this function can update state variables in the compliance contract
     *  these state variables being used by `canTransfer` to decide if a transfer
     *  is compliant or not depending on the values stored in these state variables and on
     *  the parameters of the compliance smart contract
     *  @param _from The address of the receiver
     *  @param _amount The amount of tokens involved in the transfer
     */
    function destroyed(address _from, uint256 _amount) external;

    /**
     *  @dev Returns true if the address given corresponds to a token that is bound with the Compliance contract
     *  @param _token address of the token
     */
    function isTokenBound(address _token) external view returns (bool);

    /**
     *  @dev checks that the transfer is compliant.
     *  default compliance always returns true
     *  READ ONLY FUNCTION, this function cannot be used to increment
     *  counters, emit events, ...
     *  @param _from The address of the sender
     *  @param _to The address of the receiver
     *  @param _amount The amount of tokens involved in the transfer
     */
    function canTransfer(
        address _from,
        address _to,
        uint256 _amount
    ) external view returns (bool);
}

contracts/registry/ClaimIssuersRegistry..content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@onchain-id/solidity/contracts/interface/IClaimIssuer.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/IClaimIssuersRegistry.sol";

/// @title ERC-3643 - ClaimIssuersRegistry
/// @dev This contract maintains a registry of claim issuers and their associated claim topics for the ERC-3643 standard.
contract ClaimIssuersRegistry is IClaimIssuersRegistry, Ownable {
    /// @dev Array containing all ClaimIssuers identity contract address.
    IClaimIssuer[] private _claimIssuers;

    /// @dev Mapping between a claim issuer address and its corresponding claimTopics.
    mapping(IClaimIssuer => uint256[]) private _claimIssuerClaimTopics;

    /// @dev Mapping between a claim topic and the allowed claim issuers for it.
    mapping(uint256 => IClaimIssuer[]) private _claimTopicToClaimIssuers;

    /// @notice Adds a claim issuer to the Claim Issuers Registry.
    /// @param _claimIssuer The address of the claim issuer.
    /// @param _claimTopics An array of claim topics associated with the claim issuer.
    /// Requirements:
    /// - The caller must be the owner of the contract.
    /// - The claim issuer address must not be zero.
    /// - The claim issuer must not already exist in the registry.
    /// - The claim topics array must not be empty.
    /// - It is recommended to add a reasonable number of claim issuers at once.
    /// Emits a ClaimIssuerAdded event.
    function addClaimIssuer(
        IClaimIssuer _claimIssuer,
        uint256[] calldata _claimTopics
    ) external onlyOwner {
        require(
            address(_claimIssuer) != address(0),
            "ERC-3643: Invalid zero address"
        );
        require(
            _claimIssuerClaimTopics[_claimIssuer].length == 0,
            "ERC-3643: Issuer already exists"
        );
        uint length = _claimTopics.length;
        require(length != 0, "ERC-3643: Empty claim topics");

        _claimIssuers.push(_claimIssuer);
        _claimIssuerClaimTopics[_claimIssuer] = _claimTopics;

        for (uint256 i = 0; i < length; ) {
            _claimTopicToClaimIssuers[_claimTopics[i]].push(_claimIssuer);
            unchecked {
                ++i;
            }
        }

        emit ClaimIssuerAdded(_claimIssuer, _claimTopics);
    }

    /// @notice Removes a claim issuer from the Claim Issuers Registry.
    /// @param _claimIssuer The address of the claim issuer to be removed.
    /// Requirements:
    /// - The caller must be the owner of the contract.
    /// - The claim issuer must exist in the registry.
    /// Emits a ClaimIssuerRemoved event.
    function removeClaimIssuer(IClaimIssuer _claimIssuer) external onlyOwner {
        uint claimIssuerTopicsLength = _claimIssuerClaimTopics[_claimIssuer]
            .length;
        require(claimIssuerTopicsLength != 0, "ERC-3643: Not a claim issuer");
        uint256 claimIssuerlength = _claimIssuers.length;
        for (uint256 i = 0; i < claimIssuerlength; ) {
            if (_claimIssuers[i] == _claimIssuer) {
                _claimIssuers[i] = _claimIssuers[claimIssuerlength - 1];
                _claimIssuers.pop();
                break;
            }
            unchecked {
                ++i;
            }
        }

        _removeClaimIssuerFromAllClaimTopics(
            _claimIssuer,
            claimIssuerTopicsLength
        );

        delete _claimIssuerClaimTopics[_claimIssuer];
        emit ClaimIssuerRemoved(_claimIssuer);
    }

    /// @notice Updates the claim topics associated with a claim issuer.
    /// @param _claimIssuer The address of the claim issuer.
    /// @param _claimTopics An array of claim topics to be associated with the claim issuer.
    /// Requirements:
    /// - The caller must be the owner of the contract.
    /// - The claim issuer must exist in the registry.
    /// - The claim topics array must not be empty.
    /// Emits a ClaimTopicsUpdated event.
    function updateIssuerClaimTopics(
        IClaimIssuer _claimIssuer,
        uint256[] calldata _claimTopics
    ) external onlyOwner {
        require(_claimTopics.length != 0, "ERC-3643: No claim topics");
        uint claimIssuerTopicsLength = _claimIssuerClaimTopics[_claimIssuer]
            .length;
        require(claimIssuerTopicsLength != 0, "ERC-3643: Not a claim issuer");

        _updateIssuerAcrossAllTopics(_claimIssuer);

        _claimIssuerClaimTopics[_claimIssuer] = _claimTopics;

        emit ClaimTopicsUpdated(_claimIssuer, _claimTopics);
    }

    /// @notice Returns an array of all claim issuers in the registry.
    /// @return A memory array of claim issuers.
    function getClaimIssuers() external view returns (IClaimIssuer[] memory) {
        return _claimIssuers;
    }

    /// @notice Returns an array of all claim issuers associated with a specific claim topic.
    /// @param claimTopic The claim topic to find associated claim issuers for.
    /// @return A memory array of claim issuers.
    function getClaimIssuersForClaimTopic(
        uint256 claimTopic
    ) external view returns (IClaimIssuer[] memory) {
        return _claimTopicToClaimIssuers[claimTopic];
    }

    /// @notice Checks if an address is a claim issuer in the registry.
    /// @param _issuer The address to check.
    /// @return True if the address is a claim issuer, false otherwise.
    function isClaimIssuer(IClaimIssuer _issuer) external view returns (bool) {
        return _isClaimIssuer(_issuer);
    }

    /// @notice Returns an array of claim topics associated with a specific claim issuer.
    /// @param _claimIssuer The claim issuer to find associated claim topics for.
    /// @return A memory array of claim topics.
    function getClaimIssuerClaimTopics(
        IClaimIssuer _claimIssuer
    ) external view returns (uint256[] memory) {
        require(_isClaimIssuer(_claimIssuer), "ERC-3643: Issuer doesn't exist");
        return _claimIssuerClaimTopics[_claimIssuer];
    }

    /// @notice Checks if a claim issuer has a specific claim topic.
    /// @dev This function checks if a specific claim topic is associated with a claim issuer.
    /// @param _issuer The claim issuer to check.
    /// @param _claimTopic The claim topic to check.
    /// @return bool True if the claim issuer has the claim topic, otherwise false.
    function hasClaimTopic(
        IClaimIssuer _issuer,
        uint256 _claimTopic
    ) external view returns (bool) {
        uint256 length = _claimIssuerClaimTopics[_issuer].length;
        uint256[] memory claimTopics = _claimIssuerClaimTopics[_issuer];
        for (uint256 i = 0; i < length; ) {
            if (claimTopics[i] == _claimTopic) {
                return true;
            }
            unchecked {
                ++i;
            }
        }
        return false;
    }

    /// @dev Removes a claim issuer from all associated claim topics.
    /// @param claimIssuer The claim issuer to be removed.
    /// @param length The number of claim topics associated with the claim issuer.
    function _removeClaimIssuerFromAllClaimTopics(
        IClaimIssuer claimIssuer,
        uint length
    ) private {
        for (uint256 i = 0; i < length; ) {
            uint256 claimTopic = _claimIssuerClaimTopics[claimIssuer][i];

            _removeIssuerFromTopic(claimIssuer, claimTopic);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Updates a claim issuer across all associated claim topics.
    ///      The function removes the claim issuer from each topic, and then adds it back.
    /// @param claimIssuer The claim issuer to be updated.
    function _updateIssuerAcrossAllTopics(IClaimIssuer claimIssuer) private {
        uint256[] memory claimTopics = _claimIssuerClaimTopics[claimIssuer];
        uint length = claimTopics.length;

        for (uint256 i = 0; i < length; ) {
            uint256 claimTopic = claimTopics[i];

            _removeIssuerFromTopic(claimIssuer, claimTopic);
            _claimTopicToClaimIssuers[claimTopics[i]].push(claimIssuer);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Removes a claim issuer from a specific claim topic.
    ///      The function identifies and replaces the claim issuer with the last element in the list
    ///      then removes the last element, effectively removing the issuer from the list.
    /// @param claimIssuer The claim issuer to be removed.
    /// @param claimTopic The claim topic identifier from which the issuer is to be removed.
    function _removeIssuerFromTopic(
        IClaimIssuer claimIssuer,
        uint claimTopic
    ) private {
        IClaimIssuer[] memory claimIssuers = _claimTopicToClaimIssuers[
            claimTopic
        ];
        uint length = claimIssuers.length;

        for (uint j = 0; j < length; ) {
            if (claimIssuers[j] == claimIssuer) {
                _claimTopicToClaimIssuers[claimTopic][j] = claimIssuers[
                    length - 1
                ];
                _claimTopicToClaimIssuers[claimTopic].pop();
                break;
            }
            unchecked {
                ++j;
            }
        }
    }

    /// @dev Checks if an address is a claim issuer.
    /// @param _issuer The address to check.
    /// @return bool Returns true if the address is a claim issuer, and false otherwise.
    function _isClaimIssuer(IClaimIssuer _issuer) private view returns (bool) {
        return (_claimIssuerClaimTopics[_issuer].length != 0);
    }
}

contracts/registry/ClaimTopicsRegistry.s.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/IClaimTopicsRegistry.sol";

/// @title ERC-3643 - ClaimTopicsRegistry
/// @dev A registry for managing claim topics.
contract ClaimTopicsRegistry is IClaimTopicsRegistry, Ownable {
    /// @dev An array to hold all required claim topics.
    uint256[] private _claimTopics;

    /// @notice Adds a claim topic to the registry.
    /// @dev Can only be called by the owner of the contract.
    /// Emits a ClaimTopicAdded event.
    /// @param _claimTopic The claim topic to add.
    function addClaimTopic(uint256 _claimTopic) external onlyOwner {
        require(_isClaimTopicUnique(_claimTopic), "ERC-3643: Topic exists");

        _claimTopics.push(_claimTopic);
        emit ClaimTopicAdded(_claimTopic);
    }

    /// @notice Removes a claim topic from the registry.
    /// @dev Can only be called by the owner of the contract.
    /// Emits a ClaimTopicRemoved event.
    /// @param _claimTopic The claim topic to remove.
    function removeClaimTopic(uint256 _claimTopic) external onlyOwner {
        uint256 length = _claimTopics.length;
        for (uint256 i = 0; i < length; i++) {
            if (_claimTopics[i] == _claimTopic) {
                _claimTopics[i] = _claimTopics[length - 1];
                _claimTopics.pop();
                emit ClaimTopicRemoved(_claimTopic);
                break;
            }
        }
    }

    /// @notice Retrieves all claim topics from the registry.
    /// @return uint256[] An array of claim topics.
    function getClaimTopics() external view returns (uint256[] memory) {
        return _claimTopics;
    }

    /// @notice Checks if a claim topic is unique in the registry.
    /// @dev Private function to check the uniqueness of a claim topic.
    /// @param claimTopic The claim topic to check.
    /// @return bool True if the claim topic is unique, false otherwise.
    function _isClaimTopicUnique(
        uint256 claimTopic
    ) private view returns (bool) {
        uint256[] memory claimTopics = _claimTopics;
        uint256 length = _claimTopics.length;
        for (uint256 i = 0; i < length; ) {
            if (claimTopics[i] == claimTopic) {
                return false;
            }
            unchecked {
                ++i;
            }
        }
        return true;
    }
}

contracts/registry/IdentityRegistry.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@onchain-id/solidity/contracts/interface/IClaimIssuer.sol";
import "@onchain-id/solidity/contracts/interface/IIdentity.sol";
import "./interface/IClaimTopicsRegistry.sol";
import "./interface/IClaimIssuersRegistry.sol";
import "./interface/IIdentityRegistry.sol";
import "./interface/IIdentityRegistryStorage.sol";

/// @title ERC-3643 - IdentityRegistry
/// @dev This contract is used to manage identities in the ERC-3643 standard.
/// It allows for the registration, updating and deletion of identities associated with user addresses.
/// It also supports the management of claim topics and claim issuers.
contract IdentityRegistry is IIdentityRegistry, AccessControl {
    /// @notice The address of the ClaimTopicsRegistry contract.
    IClaimTopicsRegistry private _tokenTopicsRegistry;

    /// @notice The address of the ClaimIssuersRegistry contract.
    IClaimIssuersRegistry private _tokenIssuersRegistry;

    /// @notice The address of the IdentityRegistryStorage contract.
    IIdentityRegistryStorage private _tokenIdentityStorage;

    // keccak256(AGENT_ROLE)
    bytes32 public constant AGENT_ROLE =
        0xcab5a0bfe0b79d2c4b1c2e02599fa044d115b7511f9659307cb4276950967709;

    // keccak256(OWNER_ROLE)
    bytes32 public constant OWNER_ROLE =
        0xb19546dff01e856fb3f010c267a7b1c60363cf8a4664e21cc89c26224620214e;

    /// @dev Constructor of the IdentityRegistry contract.
    /// @param _claimIssuersRegistry The address of the claim issuers registry contract.
    /// @param _claimTopicsRegistry The address of the claim topics registry contract.
    /// @param _identityStorage The address of the identity registry storage contract.
    /// @notice This constructor sets the initial state of the IdentityRegistry contract.
    constructor(
        IClaimIssuersRegistry _claimIssuersRegistry,
        IClaimTopicsRegistry _claimTopicsRegistry,
        IIdentityRegistryStorage _identityStorage
    ) {
        require(
            address(_claimIssuersRegistry) != address(0) &&
                address(_claimTopicsRegistry) != address(0) &&
                address(_identityStorage) != address(0),
            "ERC-3643: Invalid zero address"
        );
        _grantRole(bytes32(0), _msgSender());
        _grantRole(OWNER_ROLE, _msgSender());
        _tokenTopicsRegistry = _claimTopicsRegistry;
        _tokenIssuersRegistry = _claimIssuersRegistry;
        _tokenIdentityStorage = _identityStorage;
        emit ClaimTopicsRegistrySet(_claimTopicsRegistry);
        emit ClaimIssuersRegistrySet(_claimIssuersRegistry);
        emit IdentityStorageSet(_identityStorage);
    }

    /// @notice Register an identity associated with a user address.
    /// @param _userAddress The address of the user.
    /// @param _identity The identity of the user.
    /// @param _country The country code of the user.
    /// @dev Only an agent can register an identity.
    function registerIdentity(
        address _userAddress,
        IIdentity _identity,
        uint16 _country
    ) external onlyRole(AGENT_ROLE) {
        _registerIdentity(_userAddress, _identity, _country);
    }

    /// @notice Register multiple identities associated with multiple user addresses.
    /// @param _userAddresses The array of user addresses.
    /// @param _identities The array of identities.
    /// @param _countries The array of country codes.
    /// @dev Only an agent can register identities in batch.
    function batchRegisterIdentity(
        address[] calldata _userAddresses,
        IIdentity[] calldata _identities,
        uint16[] calldata _countries
    ) external onlyRole(AGENT_ROLE) {
        uint length = _userAddresses.length;
        require(length == _identities.length, "ERC-3643: Array size mismatch");
        require(length == _countries.length, "ERC-3643: Array size mismatch");
        for (uint256 i = 0; i < length; ) {
            _registerIdentity(_userAddresses[i], _identities[i], _countries[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice Update the identity associated with a user address.
    /// @param _userAddress The address of the user.
    /// @param _identity The new identity of the user.
    /// @dev Only an agent can update an identity.
    function updateIdentity(
        address _userAddress,
        IIdentity _identity
    ) external onlyRole(AGENT_ROLE) {
        IIdentity oldIdentity = _getIdentity(_userAddress);
        _tokenIdentityStorage.modifyStoredIdentity(_userAddress, _identity);
        emit IdentityUpdated(oldIdentity, _identity);
    }

    /// @notice Update the country code associated with a user address.
    /// @param _userAddress The address of the user.
    /// @param _country The new country code of the user.
    /// @dev Only an agent can update a country code.
    function updateCountry(
        address _userAddress,
        uint16 _country
    ) external onlyRole(AGENT_ROLE) {
        _tokenIdentityStorage.modifyStoredInvestorCountry(
            _userAddress,
            _country
        );
        emit CountryUpdated(_userAddress, _country);
    }

    /// @notice Delete the identity associated with a user address.
    /// @param _userAddress The address of the user.
    /// @dev Only an agent can delete an identity.
    function deleteIdentity(
        address _userAddress
    ) external onlyRole(AGENT_ROLE) {
        IIdentity oldIdentity = _getIdentity(_userAddress);
        _tokenIdentityStorage.removeIdentityFromStorage(_userAddress);
        emit IdentityRemoved(_userAddress, oldIdentity);
    }

    /// @notice Set the IdentityRegistryStorage contract.
    /// @param _identityRegistryStorage The address of the new IdentityRegistryStorage contract.
    /// @dev Only the owner can set the IdentityRegistryStorage contract.
    function setIdentityRegistryStorage(
        IIdentityRegistryStorage _identityRegistryStorage
    ) external onlyRole(OWNER_ROLE) {
        _tokenIdentityStorage = _identityRegistryStorage;
        emit IdentityStorageSet(_identityRegistryStorage);
    }

    /// @notice Set the ClaimTopicsRegistry contract.
    /// @param _claimTopicsRegistry The address of the new ClaimTopicsRegistry contract.
    /// @dev Only the owner can set the ClaimTopicsRegistry contract.
    function setClaimTopicsRegistry(
        IClaimTopicsRegistry _claimTopicsRegistry
    ) external onlyRole(OWNER_ROLE) {
        _tokenTopicsRegistry = _claimTopicsRegistry;
        emit ClaimTopicsRegistrySet(_claimTopicsRegistry);
    }

    /// @notice Set the ClaimIssuersRegistry contract.
    /// @param _claimIssuersRegistry The address of the new ClaimIssuersRegistry contract.
    /// @dev Only the owner can set the ClaimIssuersRegistry contract.
    function setClaimIssuersRegistry(
        IClaimIssuersRegistry _claimIssuersRegistry
    ) external onlyRole(OWNER_ROLE) {
        _tokenIssuersRegistry = _claimIssuersRegistry;
        emit ClaimIssuersRegistrySet(_claimIssuersRegistry);
    }

    /// @notice Checks if a user is verified based on their identity, claim topics, and claim issuers.
    /// @param _userAddress The address of the user to check.
    /// @return A boolean indicating if the user is verified.
    function isVerified(address _userAddress) external view returns (bool) {
        // Get the identity of the user from the given address
        IIdentity userIdentity = _getIdentity(_userAddress);

        // If the user identity is not set (address is 0), return false
        if (address(userIdentity) == address(0)) return false;

        // Get the required claim topics for the token
        uint256[] memory claimTopics = _tokenTopicsRegistry.getClaimTopics();
        uint claimTopicsLength = claimTopics.length;

        // If there are no required claim topics, return true
        if (claimTopicsLength == 0) return true;

        // Loop over all required claim topics
        for (uint256 i = 0; i < claimTopicsLength; i++) {
            if (!_isClaimValid(userIdentity, claimTopics[i])) {
                return false;
            }
        }
        // If all checks pass, return true
        return true;
    }

    /// @notice Get the country of an investor.
    /// @param _userAddress The address of the investor.
    /// @return The country of the investor.
    function investorCountry(
        address _userAddress
    ) external view returns (uint16) {
        return _tokenIdentityStorage.storedInvestorCountry(_userAddress);
    }

    /// @notice Get the issuers registry.
    /// @return The current issuers registry.
    function issuersRegistry() external view returns (IClaimIssuersRegistry) {
        return _tokenIssuersRegistry;
    }

    /// @notice Get the topics registry.
    /// @return The current topics registry.
    function topicsRegistry() external view returns (IClaimTopicsRegistry) {
        return _tokenTopicsRegistry;
    }

    /// @notice Get the identity storage.
    /// @return The current identity storage.
    function identityStorage()
        external
        view
        returns (IIdentityRegistryStorage)
    {
        return _tokenIdentityStorage;
    }

    /// @notice Check if an address is contained in the registry.
    /// @param _userAddress The address to check.
    /// @return A boolean indicating if the address is in the registry.
    function contains(address _userAddress) external view returns (bool) {
        return address(identity(_userAddress)) == address(0) ? false : true;
    }

    /// @notice Get the identity of a user.
    /// @param _userAddress The address of the user.
    /// @return The identity of the user.
    function identity(address _userAddress) public view returns (IIdentity) {
        return _tokenIdentityStorage.storedIdentity(_userAddress);
    }

    /// @notice Register a new identity.
    /// @param _userAddress The address of the user.
    /// @param _identity The identity of the user.
    /// @param _country The country of the user.
    function _registerIdentity(
        address _userAddress,
        IIdentity _identity,
        uint16 _country
    ) private {
        _tokenIdentityStorage.addIdentityToStorage(
            _userAddress,
            _identity,
            _country
        );
        emit IdentityRegistered(_userAddress, _identity);
    }

    /// @notice Get the identity of a user.
    /// @param _userAddress The address of the user.
    /// @return The identity of the user.
    function _getIdentity(
        address _userAddress
    ) private view returns (IIdentity) {
        return _tokenIdentityStorage.storedIdentity(_userAddress);
    }

    function _isClaimValid(
        IIdentity userIdentity,
        uint256 claimTopic
    ) private view returns (bool) {
        IClaimIssuer[] memory claimIssuers = _tokenIssuersRegistry
            .getClaimIssuersForClaimTopic(claimTopic);
        uint claimIssuersLength = claimIssuers.length;

        if (claimIssuersLength == 0) {
            return false;
        }

        bytes32[] memory claimIds = new bytes32[](claimIssuersLength);

        for (uint256 i = 0; i < claimIssuersLength; i++) {
            claimIds[i] = keccak256(abi.encode(claimIssuers[i], claimTopic));
        }

        for (uint256 j = 0; j < claimIds.length; j++) {
            (
                uint256 foundClaimTopic,
                ,
                address issuer,
                bytes memory sig,
                bytes memory data,

            ) = userIdentity.getClaim(claimIds[j]);

            if (foundClaimTopic == claimTopic) {
                if (
                    _isIssuerClaimValid(
                        userIdentity,
                        issuer,
                        claimTopic,
                        sig,
                        data
                    )
                ) {
                    return true;
                }
            } else if (j == claimIds.length - 1) {
                return false;
            }
        }

        return false;
    }

    /// @param userIdentity The identity contract related to the claim.
    /// @param issuer The address of the claim issuer.
    /// @param claimTopic The claim topic of the claim.
    /// @param sig The signature of the claim.
    /// @param data The data field of the claim.
    /// @return claimValid True if the claim is valid, false otherwise.
    function _isIssuerClaimValid(
        IIdentity userIdentity,
        address issuer,
        uint claimTopic,
        bytes memory sig,
        bytes memory data
    ) private view returns (bool) {
        try
            IClaimIssuer(issuer).isClaimValid(
                userIdentity,
                claimTopic,
                sig,
                data
            )
        returns (bool _validity) {
            return _validity;
        } catch {
            return false;
        }
    }
}

contracts/registry/IdentityRegistryStora.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@onchain-id/solidity/contracts/interface/IIdentity.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

import "./interface/IIdentityRegistryStorage.sol";

/// @title ERC-3643 - IdentityRegistryStorage
/// @notice Stores user identities and their respective countries.
contract IdentityRegistryStorage is IIdentityRegistryStorage, AccessControl {
    /// @dev struct containing the identity contract and the country of the user
    struct Identity {
        /// @dev Identity contract of the user
        IIdentity identityContract;
        /// @dev Country of the user
        uint16 investorCountry;
    }

    // keccak256(AGENT_ROLE)
    bytes32 public constant AGENT_ROLE =
        0xcab5a0bfe0b79d2c4b1c2e02599fa044d115b7511f9659307cb4276950967709;

    // keccak256(OWNER_ROLE)
    bytes32 public constant OWNER_ROLE =
        0xb19546dff01e856fb3f010c267a7b1c60363cf8a4664e21cc89c26224620214e;

    /// @dev Mapping between a user address and the corresponding identity
    mapping(address => Identity) internal _identities;

    /// @dev Array of Identity Registries linked to this storage
    address[] internal _identityRegistries;

    constructor() {
        _grantRole(bytes32(0), _msgSender());
        _grantRole(AGENT_ROLE, _msgSender());
        _grantRole(OWNER_ROLE, _msgSender());
    }

    /// @notice Adds a new identity to the storage
    /// @param _userAddress User's address
    /// @param _identity Identity contract of the user
    /// @param _country Country of the user
    function addIdentityToStorage(
        address _userAddress,
        IIdentity _identity,
        uint16 _country
    ) external onlyRole(AGENT_ROLE) {
        require(
            _userAddress != address(0) && address(_identity) != address(0),
            "ERC-3643: Invalid zero address"
        );
        require(
            address(_identities[_userAddress].identityContract) == address(0),
            "ERC-3643: Already stored"
        );
        _identities[_userAddress].identityContract = _identity;
        _identities[_userAddress].investorCountry = _country;
        emit IdentityStored(_userAddress, _identity);
    }

    /// @notice Modifies the stored identity of a user
    /// @param _userAddress User's address
    /// @param _identity New identity contract of the user
    function modifyStoredIdentity(
        address _userAddress,
        IIdentity _identity
    ) external onlyRole(AGENT_ROLE) {
        require(
            _userAddress != address(0) && address(_identity) != address(0),
            "ERC-3643: Invalid zero address"
        );
        require(
            address(_identities[_userAddress].identityContract) != address(0),
            "ERC-3643: Address not stored"
        );
        IIdentity oldIdentity = _identities[_userAddress].identityContract;
        _identities[_userAddress].identityContract = _identity;
        emit IdentityModified(oldIdentity, _identity);
    }

    /// @notice Modifies the stored investor country of a user
    /// @param _userAddress User's address
    /// @param _country New country of the user
    function modifyStoredInvestorCountry(
        address _userAddress,
        uint16 _country
    ) external onlyRole(AGENT_ROLE) {
        require(_userAddress != address(0), "ERC-3643: Invalid zero address");
        require(
            address(_identities[_userAddress].identityContract) != address(0),
            "ERC-3643: Address not stored"
        );
        _identities[_userAddress].investorCountry = _country;
        emit CountryModified(_userAddress, _country);
    }

    /// @notice Removes a user identity from the storage
    /// @param _userAddress User's address
    function removeIdentityFromStorage(
        address _userAddress
    ) external onlyRole(AGENT_ROLE) {
        require(_userAddress != address(0), "ERC-3643: Invalid zero address");
        require(
            address(_identities[_userAddress].identityContract) != address(0),
            "ERC-3643: Address not stored"
        );
        IIdentity oldIdentity = _identities[_userAddress].identityContract;
        delete _identities[_userAddress];
        emit IdentityUnstored(_userAddress, oldIdentity);
    }

    /// @notice Links an identity registry to this storage
    /// @param _identityRegistry Address of the identity registry
    function bindIdentityRegistry(
        address _identityRegistry
    ) external onlyRole(OWNER_ROLE) {
        require(
            _identityRegistry != address(0),
            "ERC-3643: Invalid zero address"
        );
        _grantRole(AGENT_ROLE, _identityRegistry);
        _identityRegistries.push(_identityRegistry);
        emit IdentityRegistryBound(_identityRegistry);
    }

    /// @notice Unlinks an identity registry from this storage
    /// @param _identityRegistry Address of the identity registry
    function unbindIdentityRegistry(
        address _identityRegistry
    ) external onlyRole(OWNER_ROLE) {
        require(
            _identityRegistry != address(0),
            "ERC-3643: Invalid zero address"
        );
        require(
            _identityRegistries.length != 0,
            "ERC-3643: No identity registry"
        );
        uint256 length = _identityRegistries.length;
        for (uint256 i = 0; i < length; ) {
            if (_identityRegistries[i] == _identityRegistry) {
                _identityRegistries[i] = _identityRegistries[length - 1];
                _identityRegistries.pop();
                break;
            }
            unchecked {
                ++i;
            }
        }
        _revokeRole(AGENT_ROLE, _identityRegistry);
        emit IdentityRegistryUnbound(_identityRegistry);
    }

    /// @notice Returns all linked identity registries
    /// @return Array of addresses of the linked identity registries
    function linkedIdentityRegistries()
        external
        view
        returns (address[] memory)
    {
        return _identityRegistries;
    }

    /// @notice Returns the stored identity of a user
    /// @param _userAddress User's address
    /// @return User's identity contract
    function storedIdentity(
        address _userAddress
    ) external view returns (IIdentity) {
        return _identities[_userAddress].identityContract;
    }

    /// @notice Returns the stored investor country of a user
    /// @param _userAddress User's address
    /// @return User's country
    function storedInvestorCountry(
        address _userAddress
    ) external view returns (uint16) {
        return _identities[_userAddress].investorCountry;
    }
}

contracts/registry/interface/IClaimIssue.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@onchain-id/solidity/contracts/interface/IClaimIssuer.sol";

interface IClaimIssuersRegistry {
    /**
     *  this event is emitted when a claim issuer is added in the registry.
     *  the event is emitted by the addClaimIssuer function
     *  `claimIssuer` is the address of the claim issuer's ClaimIssuer contract
     *  `claimTopics` is the set of claims that the claim issuer is allowed to emit
     */
    event ClaimIssuerAdded(
        IClaimIssuer indexed claimIssuer,
        uint256[] claimTopics
    );

    /**
     *  this event is emitted when a claim issuer is removed from the registry.
     *  the event is emitted by the removeClaimIssuer function
     *  `claimIssuer` is the address of the claim issuer's ClaimIssuer contract
     */
    event ClaimIssuerRemoved(IClaimIssuer indexed claimIssuer);

    /**
     *  this event is emitted when the set of claim topics is changed for a given claim issuer.
     *  the event is emitted by the updateIssuerClaimTopics function
     *  `claimIssuer` is the address of the claim issuer's ClaimIssuer contract
     *  `claimTopics` is the set of claims that the claim issuer is allowed to emit
     */
    event ClaimTopicsUpdated(
        IClaimIssuer indexed claimIssuer,
        uint256[] claimTopics
    );

    /**
     *  @dev registers a ClaimIssuer contract as claim claim issuer.
     *  Requires that a ClaimIssuer contract doesn't already exist
     *  Requires that the claimTopics set is not empty
     *  Requires that there is no more than 15 claimTopics
     *  Requires that there is no more than 50 Claim issuers
     *  @param _claimIssuer The ClaimIssuer contract address of the claim claim issuer.
     *  @param _claimTopics the set of claim topics that the claim issuer is allowed to emit
     *  This function can only be called by the owner of the Claim Issuers Registry contract
     *  emits a `ClaimIssuerAdded` event
     */
    function addClaimIssuer(
        IClaimIssuer _claimIssuer,
        uint256[] calldata _claimTopics
    ) external;

    /**
     *  @dev Removes the ClaimIssuer contract of a claim claim issuer.
     *  Requires that the claim issuer contract to be registered first
     *  @param _claimIssuer the claim issuer to remove.
     *  This function can only be called by the owner of the Claim Issuers Registry contract
     *  emits a `ClaimIssuerRemoved` event
     */
    function removeClaimIssuer(IClaimIssuer _claimIssuer) external;

    /**
     *  @dev Updates the set of claim topics that a claim issuer is allowed to emit.
     *  Requires that this ClaimIssuer contract already exists in the registry
     *  Requires that the provided claimTopics set is not empty
     *  Requires that there is no more than 15 claimTopics
     *  @param _claimIssuer the claim issuer to update.
     *  @param _claimTopics the set of claim topics that the claim issuer is allowed to emit
     *  This function can only be called by the owner of the Claim Issuers Registry contract
     *  emits a `ClaimTopicsUpdated` event
     */
    function updateIssuerClaimTopics(
        IClaimIssuer _claimIssuer,
        uint256[] calldata _claimTopics
    ) external;

    /**
     *  @dev Function for getting all the claim claim issuers stored.
     *  @return array of all claim issuers registered.
     */
    function getClaimIssuers() external view returns (IClaimIssuer[] memory);

    /**
     *  @dev Function for getting all the claim issuer allowed for a given claim topic.
     *  @param claimTopic the claim topic to get the claim issuers for.
     *  @return array of all claim issuer addresses that are allowed for the given claim topic.
     */
    function getClaimIssuersForClaimTopic(
        uint256 claimTopic
    ) external view returns (IClaimIssuer[] memory);

    /**
     *  @dev Checks if the ClaimIssuer contract is claim
     *  @param _issuer the address of the ClaimIssuer contract
     *  @return true if the issuer is claim, false otherwise.
     */
    function isClaimIssuer(IClaimIssuer _issuer) external view returns (bool);

    /**
     *  @dev Function for getting all the claim topic of claim claim issuer
     *  Requires the provided ClaimIssuer contract to be registered in the claim issuers registry.
     *  @param _claimIssuer the claim issuer concerned.
     *  @return The set of claim topics that the claim issuer is allowed to emit
     */
    function getClaimIssuerClaimTopics(
        IClaimIssuer _claimIssuer
    ) external view returns (uint256[] memory);

    /**
     *  @dev Function for checking if the claim claim issuer is allowed
     *  to emit a certain claim topic
     *  @param _issuer the address of the claim issuer's ClaimIssuer contract
     *  @param _claimTopic the Claim Topic that has to be checked to know if the `issuer` is allowed to emit it
     *  @return true if the issuer is claim for this claim topic.
     */
    function hasClaimTopic(
        IClaimIssuer _issuer,
        uint256 _claimTopic
    ) external view returns (bool);
}

contracts/registry/interface/IClaimTopic.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

interface IClaimTopicsRegistry {
    /**
     *  this event is emitted when a claim topic has been added to the ClaimTopicsRegistry
     *  the event is emitted by the 'addClaimTopic' function
     *  `claimTopic` is the required claim added to the Claim Topics Registry
     */
    event ClaimTopicAdded(uint256 indexed claimTopic);

    /**
     *  this event is emitted when a claim topic has been removed from the ClaimTopicsRegistry
     *  the event is emitted by the 'removeClaimTopic' function
     *  `claimTopic` is the required claim removed from the Claim Topics Registry
     */
    event ClaimTopicRemoved(uint256 indexed claimTopic);

    /**
     * @dev Add a claim claim topic (For example: KYC=1, AML=2).
     * Only owner can call.
     * emits `ClaimTopicAdded` event
     * cannot add more than 15 topics for 1 token as adding more could create gas issues
     * @param _claimTopic The claim topic index
     */
    function addClaimTopic(uint256 _claimTopic) external;

    /**
     *  @dev Remove a claim claim topic (For example: KYC=1, AML=2).
     *  Only owner can call.
     *  emits `ClaimTopicRemoved` event
     *  @param _claimTopic The claim topic index
     */
    function removeClaimTopic(uint256 _claimTopic) external;

    /**
     *  @dev Get the claim claim topics for the security token
     *  @return Array of claim claim topics
     */
    function getClaimTopics() external view returns (uint256[] memory);
}

contracts/registry/interface/IIdentityRe.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "./IClaimIssuersRegistry.sol";
import "./IClaimTopicsRegistry.sol";
import "./IIdentityRegistryStorage.sol";

import "@onchain-id/solidity/contracts/interface/IClaimIssuer.sol";
import "@onchain-id/solidity/contracts/interface/IIdentity.sol";

interface IIdentityRegistry {
    /**
     *  this event is emitted when the ClaimTopicsRegistry has been set for the IdentityRegistry
     *  the event is emitted by the IdentityRegistry constructor
     *  `claimTopicsRegistry` is the address of the Claim Topics Registry contract
     */
    event ClaimTopicsRegistrySet(
        IClaimTopicsRegistry indexed claimTopicsRegistry
    );

    /**
     *  this event is emitted when the IdentityRegistryStorage has been set for the IdentityRegistry
     *  the event is emitted by the IdentityRegistry constructor
     *  `identityStorage` is the address of the Identity Registry Storage contract
     */
    event IdentityStorageSet(IIdentityRegistryStorage indexed identityStorage);

    /**
     *  this event is emitted when the ClaimIssuersRegistry has been set for the IdentityRegistry
     *  the event is emitted by the IdentityRegistry constructor
     *  `claimIssuersRegistry` is the address of the Claim Issuers Registry contract
     */
    event ClaimIssuersRegistrySet(
        IClaimIssuersRegistry indexed claimIssuersRegistry
    );

    /**
     *  this event is emitted when an Identity is registered into the Identity Registry.
     *  the event is emitted by the 'registerIdentity' function
     *  `investorAddress` is the address of the investor's wallet
     *  `identity` is the address of the Identity smart contract (onchainID)
     */
    event IdentityRegistered(
        address indexed investorAddress,
        IIdentity indexed identity
    );

    /**
     *  this event is emitted when an Identity is removed from the Identity Registry.
     *  the event is emitted by the 'deleteIdentity' function
     *  `investorAddress` is the address of the investor's wallet
     *  `identity` is the address of the Identity smart contract (onchainID)
     */
    event IdentityRemoved(
        address indexed investorAddress,
        IIdentity indexed identity
    );

    /**
     *  this event is emitted when an Identity has been updated
     *  the event is emitted by the 'updateIdentity' function
     *  `oldIdentity` is the old Identity contract's address to update
     *  `newIdentity` is the new Identity contract's
     */
    event IdentityUpdated(
        IIdentity indexed oldIdentity,
        IIdentity indexed newIdentity
    );

    /**
     *  this event is emitted when an Identity's country has been updated
     *  the event is emitted by the 'updateCountry' function
     *  `investorAddress` is the address on which the country has been updated
     *  `country` is the numeric code (ISO 3166-1) of the new country
     */
    event CountryUpdated(
        address indexed investorAddress,
        uint16 indexed country
    );

    /**
     *  @dev Register an identity contract corresponding to a user address.
     *  Requires that the user doesn't have an identity contract already registered.
     *  This function can only be called by a wallet set as agent of the smart contract
     *  @param _userAddress The address of the user
     *  @param _identity The address of the user's identity contract
     *  @param _country The country of the investor
     *  emits `IdentityRegistered` event
     */
    function registerIdentity(
        address _userAddress,
        IIdentity _identity,
        uint16 _country
    ) external;

    /**
     *  @dev Removes an user from the identity registry.
     *  Requires that the user have an identity contract already deployed that will be deleted.
     *  This function can only be called by a wallet set as agent of the smart contract
     *  @param _userAddress The address of the user to be removed
     *  emits `IdentityRemoved` event
     */
    function deleteIdentity(address _userAddress) external;

    /**
     *  @dev Replace the actual identityRegistryStorage contract with a new one.
     *  This function can only be called by the wallet set as owner of the smart contract
     *  @param _identityRegistryStorage The address of the new Identity Registry Storage
     *  emits `IdentityStorageSet` event
     */
    function setIdentityRegistryStorage(
        IIdentityRegistryStorage _identityRegistryStorage
    ) external;

    /**
     *  @dev Replace the actual claimTopicsRegistry contract with a new one.
     *  This function can only be called by the wallet set as owner of the smart contract
     *  @param _claimTopicsRegistry The address of the new claim Topics Registry
     *  emits `ClaimTopicsRegistrySet` event
     */
    function setClaimTopicsRegistry(
        IClaimTopicsRegistry _claimTopicsRegistry
    ) external;

    /**
     *  @dev Replace the actual claimIssuersRegistry contract with a new one.
     *  This function can only be called by the wallet set as owner of the smart contract
     *  @param _claimIssuersRegistry The address of the new Claim Issuers Registry
     *  emits `ClaimIssuersRegistrySet` event
     */
    function setClaimIssuersRegistry(
        IClaimIssuersRegistry _claimIssuersRegistry
    ) external;

    /**
     *  @dev Updates the country corresponding to a user address.
     *  Requires that the user should have an identity contract already deployed that will be replaced.
     *  This function can only be called by a wallet set as agent of the smart contract
     *  @param _userAddress The address of the user
     *  @param _country The new country of the user
     *  emits `CountryUpdated` event
     */
    function updateCountry(address _userAddress, uint16 _country) external;

    /**
     *  @dev Updates an identity contract corresponding to a user address.
     *  Requires that the user address should be the owner of the identity contract.
     *  Requires that the user should have an identity contract already deployed that will be replaced.
     *  This function can only be called by a wallet set as agent of the smart contract
     *  @param _userAddress The address of the user
     *  @param _identity The address of the user's new identity contract
     *  emits `IdentityUpdated` event
     */
    function updateIdentity(address _userAddress, IIdentity _identity) external;

    /**
     *  @dev function allowing to register identities in batch
     *  This function can only be called by a wallet set as agent of the smart contract
     *  Requires that none of the users has an identity contract already registered.
     *  IMPORTANT : THIS TRANSACTION COULD EXCEED GAS LIMIT IF `_userAddresses.length` IS TOO HIGH,
     *  USE WITH CARE OR YOU COULD LOSE TX FEES WITH AN "OUT OF GAS" TRANSACTION
     *  @param _userAddresses The addresses of the users
     *  @param _identities The addresses of the corresponding identity contracts
     *  @param _countries The countries of the corresponding investors
     *  emits _userAddresses.length `IdentityRegistered` events
     */
    function batchRegisterIdentity(
        address[] calldata _userAddresses,
        IIdentity[] calldata _identities,
        uint16[] calldata _countries
    ) external;

    /**
     *  @dev This functions checks whether a wallet has its Identity registered or not
     *  in the Identity Registry.
     *  @param _userAddress The address of the user to be checked.
     *  @return 'True' if the address is contained in the Identity Registry, 'false' if not.
     */
    function contains(address _userAddress) external view returns (bool);

    /**
     *  @dev This functions checks whether an identity contract
     *  corresponding to the provided user address has the required claims or not based
     *  on the data fetched from claim issuers registry and from the claim topics registry
     *  @param _userAddress The address of the user to be verified.
     *  @return 'True' if the address is verified, 'false' if not.
     */
    function isVerified(address _userAddress) external view returns (bool);

    /**
     *  @dev Returns the onchainID of an investor.
     *  @param _userAddress The wallet of the investor
     */
    function identity(address _userAddress) external view returns (IIdentity);

    /**
     *  @dev Returns the country code of an investor.
     *  @param _userAddress The wallet of the investor
     */
    function investorCountry(
        address _userAddress
    ) external view returns (uint16);

    /**
     *  @dev Returns the IdentityRegistryStorage linked to the current IdentityRegistry.
     */
    function identityStorage() external view returns (IIdentityRegistryStorage);

    /**
     *  @dev Returns the ClaimIssuersRegistry linked to the current IdentityRegistry.
     */
    function issuersRegistry() external view returns (IClaimIssuersRegistry);

    /**
     *  @dev Returns the ClaimTopicsRegistry linked to the current IdentityRegistry.
     */
    function topicsRegistry() external view returns (IClaimTopicsRegistry);
}

// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "@onchain-id/solidity/contracts/interface/IIdentity.sol";

interface IIdentityRegistryStorage {
    /// events

    /**
     *  this event is emitted when an Identity is registered into the storage contract.
     *  the event is emitted by the 'registerIdentity' function
     *  `investorAddress` is the address of the investor's wallet
     *  `identity` is the address of the Identity smart contract (onchainID)
     */
    event IdentityStored(
        address indexed investorAddress,
        IIdentity indexed identity
    );

    /**
     *  this event is emitted when an Identity is removed from the storage contract.
     *  the event is emitted by the 'deleteIdentity' function
     *  `investorAddress` is the address of the investor's wallet
     *  `identity` is the address of the Identity smart contract (onchainID)
     */
    event IdentityUnstored(
        address indexed investorAddress,
        IIdentity indexed identity
    );

    /**
     *  this event is emitted when an Identity has been updated
     *  the event is emitted by the 'updateIdentity' function
     *  `oldIdentity` is the old Identity contract's address to update
     *  `newIdentity` is the new Identity contract's
     */
    event IdentityModified(
        IIdentity indexed oldIdentity,
        IIdentity indexed newIdentity
    );

    /**
     *  this event is emitted when an Identity's country has been updated
     *  the event is emitted by the 'updateCountry' function
     *  `investorAddress` is the address on which the country has been updated
     *  `country` is the numeric code (ISO 3166-1) of the new country
     */
    event CountryModified(
        address indexed investorAddress,
        uint16 indexed country
    );

    /**
     *  this event is emitted when an Identity Registry is bound to the storage contract
     *  the event is emitted by the 'addIdentityRegistry' function
     *  `identityRegistry` is the address of the identity registry added
     */
    event IdentityRegistryBound(address indexed identityRegistry);

    /**
     *  this event is emitted when an Identity Registry is unbound from the storage contract
     *  the event is emitted by the 'removeIdentityRegistry' function
     *  `identityRegistry` is the address of the identity registry removed
     */
    event IdentityRegistryUnbound(address indexed identityRegistry);

    /// functions

    /**
     *  @dev adds an identity contract corresponding to a user address in the storage.
     *  Requires that the user doesn't have an identity contract already registered.
     *  This function can only be called by an address set as agent of the smart contract
     *  @param _userAddress The address of the user
     *  @param _identity The address of the user's identity contract
     *  @param _country The country of the investor
     *  emits `IdentityStored` event
     */
    function addIdentityToStorage(
        address _userAddress,
        IIdentity _identity,
        uint16 _country
    ) external;

    /**
     *  @dev Removes an user from the storage.
     *  Requires that the user have an identity contract already deployed that will be deleted.
     *  This function can only be called by an address set as agent of the smart contract
     *  @param _userAddress The address of the user to be removed
     *  emits `IdentityUnstored` event
     */
    function removeIdentityFromStorage(address _userAddress) external;

    /**
     *  @dev Updates the country corresponding to a user address.
     *  Requires that the user should have an identity contract already deployed that will be replaced.
     *  This function can only be called by an address set as agent of the smart contract
     *  @param _userAddress The address of the user
     *  @param _country The new country of the user
     *  emits `CountryModified` event
     */
    function modifyStoredInvestorCountry(
        address _userAddress,
        uint16 _country
    ) external;

    /**
     *  @dev Updates an identity contract corresponding to a user address.
     *  Requires that the user address should be the owner of the identity contract.
     *  Requires that the user should have an identity contract already deployed that will be replaced.
     *  This function can only be called by an address set as agent of the smart contract
     *  @param _userAddress The address of the user
     *  @param _identity The address of the user's new identity contract
     *  emits `IdentityModified` event
     */
    function modifyStoredIdentity(
        address _userAddress,
        IIdentity _identity
    ) external;

    /**
     *  @notice Adds an identity registry as agent of the Identity Registry Storage Contract.
     *  This function can only be called by the wallet set as owner of the smart contract
     *  This function adds the identity registry to the list of identityRegistries linked to the storage contract
     *  cannot bind more than 300 IR to 1 IRS
     *  @param _identityRegistry The identity registry address to add.
     */
    function bindIdentityRegistry(address _identityRegistry) external;

    /**
     *  @notice Removes an identity registry from being agent of the Identity Registry Storage Contract.
     *  This function can only be called by the wallet set as owner of the smart contract
     *  This function removes the identity registry from the list of identityRegistries linked to the storage contract
     *  @param _identityRegistry The identity registry address to remove.
     */
    function unbindIdentityRegistry(address _identityRegistry) external;

    /**
     *  @dev Returns the identity registries linked to the storage contract
     */
    function linkedIdentityRegistries()
        external
        view
        returns (address[] memory);

    /**
     *  @dev Returns the onchainID of an investor.
     *  @param _userAddress The wallet of the investor
     */
    function storedIdentity(
        address _userAddress
    ) external view returns (IIdentity);

    /**
     *  @dev Returns the country code of an investor.
     *  @param _userAddress The wallet of the investor
     */
    function storedInvestorCountry(
        address _userAddress
    ) external view returns (uint16);
}

contracts/token/Token.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.17;

import "./IToken.sol";
import "@onchain-id/solidity/contracts/interface/IIdentity.sol";

import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

import "../compliance/interface/ICompliance.sol";

/// @title ERC-3643 - T-Rex Token (Version RAPTOR-5.0.0)
/// @notice An ERC-3643 compliant token with onchain validators and compliance checks.
contract Token is IToken, AccessControl, Pausable {
    /// @dev ERC20 basic variables
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    /// @dev Variables of freeze and pause functions
    mapping(address => bool) private _frozen;
    mapping(address => uint256) private _frozenAmounts;

    uint256 private _totalSupply;

    /// @dev Token information
    string private _name;
    string private _symbol;
    uint8 private immutable _decimals;
    address private _onchainID;
    string private constant _TOKEN_VERSION = "RAPTOR-5.0.0";

    // keccak256(AGENT_ROLE)
    bytes32 public constant AGENT_ROLE =
        0xcab5a0bfe0b79d2c4b1c2e02599fa044d115b7511f9659307cb4276950967709;

    // keccak256(OWNER_ROLE)
    bytes32 public constant OWNER_ROLE =
        0xb19546dff01e856fb3f010c267a7b1c60363cf8a4664e21cc89c26224620214e;

    /// @dev Identity Registry contract used by the onchain validator system
    IIdentityRegistry private _identityRegistry;

    /// @dev Compliance contract linked to the onchain validator system
    ICompliance private _compliance;

    /// @dev the constructor initiates the token contract
    /// _msgSender() is set automatically as the owner of the smart contract
    /// @param identityRegistry_ the address of the Identity registry linked to the token
    /// @param compliance_ the address of the compliance contract linked to the token
    /// @param name_ the name of the token
    /// @param symbol_ the symbol of the token
    /// @param decimals_ the decimals of the token
    /// @param onchainID_ the address of the onchainID of the token
    /// emits an `UpdatedTokenInformation` event
    /// emits an `IdentityRegistryAdded` event
    /// emits a `ComplianceAdded` event
    constructor(
        address identityRegistry_,
        address compliance_,
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        address onchainID_
    ) {
        require(
            identityRegistry_ != address(0) && compliance_ != address(0),
            "ERC-3643: Invalid zero address"
        );

        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _onchainID = onchainID_;

        _grantRole(bytes32(0), _msgSender());
        _grantRole(OWNER_ROLE, _msgSender());
        _grantRole(AGENT_ROLE, _msgSender());

        _identityRegistry = IIdentityRegistry(identityRegistry_);
        _compliance = ICompliance(compliance_);
        _compliance.bindToken(address(this));

        emit IdentityRegistryAdded(identityRegistry_);
        emit ComplianceAdded(compliance_);
        emit UpdatedOnchainID(_onchainID);
    }

    /// @notice Approve `amount` tokens to be spent by `spender`
    /// @param spender The address of the account allowed to spend the tokens
    /// @param amount The number of tokens to be spent
    /// @return A boolean that indicates if the operation was successful.
    function approve(address spender, uint256 amount) external returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /// @notice ERC-20 overridden function that include logic to check for trade validity.
    /// @dev Transfer tokens to another address. Requires that the _msgSender()
    /// and to addresses are not frozen and that the value should not exceed available balance.
    /// @param to The address of the receiver.
    /// @param amount The number of tokens to transfer.
    /// @return true if the transfer is successful.
    function transfer(
        address to,
        uint256 amount
    ) external whenNotPaused returns (bool) {
        _transfer(_msgSender(), to, amount);
        return true;
    }

    /// @dev ERC-20 overridden function that include logic to check for trade validity
    /// @dev Transfer tokens from one address to another. Requires that the `from` and `to` addresses are not frozen
    /// and that the value should not exceed available balance.
    /// @param from The address of the sender.
    /// @param to The address of the receiver.
    /// @param amount The number of tokens to transfer.
    /// @return true if the transfer is successful.
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external whenNotPaused returns (bool) {
        _spendAllowance(from, _msgSender(), amount);
        _transfer(from, to, amount);

        return true;
    }

    /// @notice Increase the allowance provided to `spender` by the caller
    /// @param spender The address of the account allowed to spend the tokens
    /// @param _addedValue The increase in allowance
    /// @return A boolean that indicates if the operation was successful.
    function increaseAllowance(
        address spender,
        uint256 _addedValue
    ) external returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] + (_addedValue)
        );
        return true;
    }

    /// @notice Decrease the allowance provided to `spender` by the caller
    /// @param spender The address of the account allowed to spend the tokens
    /// @param _subtractedValue The decrease in allowance
    /// @return A boolean that indicates if the operation was successful.
    function decreaseAllowance(
        address spender,
        uint256 _subtractedValue
    ) external returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] - _subtractedValue
        );
        return true;
    }

    /// @dev Set the onchainID of a token. Can only be called by an owner of the contract.
    /// @param onchainID_ The address of the onchainID.
    /// @notice Emits an UpdatedOnchainID event.
    function setOnchainID(address onchainID_) external onlyRole(OWNER_ROLE) {
        _onchainID = onchainID_;
        emit UpdatedOnchainID(onchainID_);
    }

    /// @notice Pause all token operations
    /// @dev Can only be called by an agent of the contract
    function pause() external onlyRole(AGENT_ROLE) {
        _pause();
    }

    /// @notice Unpause all token operations
    /// @dev Can only be called by an agent of the contract
    function unpause() external onlyRole(AGENT_ROLE) {
        _unpause();
    }

    // @dev Perform a batch transfer of tokens.
    /// @param toList An array of receiver addresses.
    /// @param amounts An array of amounts to transfer.
    function batchTransfer(
        address[] calldata toList,
        uint256[] calldata amounts
    ) external whenNotPaused {
        uint length = toList.length;
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _transfer(_msgSender(), toList[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch forced transfer of tokens.
    /// @param fromList An array of sender addresses.
    /// @param toList An array of receiver addresses.
    /// @param amounts An array of amounts to transfer.
    function batchTransferFrom(
        address[] calldata fromList,
        address[] calldata toList,
        uint256[] calldata amounts
    ) external whenNotPaused {
        uint length = fromList.length;
        require(length == toList.length, "ERC-3643: Array size mismatch");
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _spendAllowance(fromList[i], _msgSender(), amounts[i]);
            _transfer(fromList[i], toList[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch forced transfer of tokens.
    /// @param fromList An array of sender addresses.
    /// @param toList An array of receiver addresses.
    /// @param amounts An array of amounts to transfer.
    function batchForcedTransfer(
        address[] calldata fromList,
        address[] calldata toList,
        uint256[] calldata amounts
    ) external onlyRole(AGENT_ROLE) {
        uint length = fromList.length;
        require(length == toList.length, "ERC-3643: Array size mismatch");
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _forcedTransfer(fromList[i], toList[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch minting of tokens.
    /// @param toList An array of receiver addresses.
    /// @param amounts An array of amounts to mint.
    function batchMint(
        address[] calldata toList,
        uint256[] calldata amounts
    ) external onlyRole(AGENT_ROLE) {
        uint length = toList.length;
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _mint(toList[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch burn of tokens.
    /// @param accounts An array of addresses from which to burn tokens.
    /// @param amounts An array of amounts to burn.
    function batchBurn(
        address[] calldata accounts,
        uint256[] calldata amounts
    ) external onlyRole(AGENT_ROLE) {
        uint length = accounts.length;
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _burn(accounts[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch freezing / unfreezing of addresses.
    /// @param accounts An array of addresses to freeze.
    /// @param freeze An array of boolean values indicating whether to freeze the corresponding address.
    function batchSetAddressFrozen(
        address[] calldata accounts,
        bool[] calldata freeze
    ) external onlyRole(AGENT_ROLE) {
        uint length = accounts.length;
        require(length == freeze.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _setAddressFrozen(accounts[i], freeze[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch freezing of partial tokens from multiple addresses.
    /// @param accounts An array of addresses from which to freeze tokens.
    /// @param amounts An array of amounts to freeze.
    function batchFreezePartialTokens(
        address[] calldata accounts,
        uint256[] calldata amounts
    ) external onlyRole(AGENT_ROLE) {
        uint length = accounts.length;
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _freezePartialTokens(accounts[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Perform a batch unfreezing of partial tokens from multiple addresses.
    /// @param accounts An array of addresses from which to unfreeze tokens.
    /// @param amounts An array of amounts to unfreeze.
    function batchUnfreezePartialTokens(
        address[] calldata accounts,
        uint256[] calldata amounts
    ) external onlyRole(AGENT_ROLE) {
        uint length = accounts.length;
        require(length == amounts.length, "ERC-3643: Array size mismatch");

        for (uint256 i = 0; i < length; ) {
            _unfreezePartialTokens(accounts[i], amounts[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @dev Recover tokens from a lost wallet and transfer them to a new wallet. Can only be called by an agent of the contract.
    /// @param lostWallet The address of the lost wallet.
    /// @param newWallet The address of the new wallet.
    /// @param investorOnchainID The onchainID of the investor.
    /// @return true if the recovery is successful.
    function recoveryAddress(
        address lostWallet,
        address newWallet,
        address investorOnchainID
    ) external onlyRole(AGENT_ROLE) returns (bool) {
        uint256 investorBalance = _balances[lostWallet];
        require(investorBalance != 0, "ERC-3643: No tokens to recover");

        IIdentity identity = IIdentity(investorOnchainID);

        bool isLostWalletFrozen = _frozen[lostWallet];
        bytes32 _key = keccak256(abi.encode(newWallet));
        require(
            identity.keyHasPurpose(_key, 1),
            "ERC-3643: Recovery not possible"
        );
        uint256 frozenTokens = _frozenAmounts[lostWallet];

        _identityRegistry.registerIdentity(
            newWallet,
            identity,
            _identityRegistry.investorCountry(lostWallet)
        );

        if (isLostWalletFrozen) _frozen[lostWallet] = false;

        _forcedTransfer(lostWallet, newWallet, investorBalance);

        if (frozenTokens != 0) {
            _freezePartialTokens(newWallet, frozenTokens);
        }
        if (isLostWalletFrozen == true) {
            _setAddressFrozen(newWallet, true);
        }
        _identityRegistry.deleteIdentity(lostWallet);

        emit RecoverySuccess(lostWallet, newWallet, investorOnchainID);

        return true;
    }

    /// @notice Executes a forced transfer of tokens from one address to another
    /// @param from The address from which the tokens will be transferred
    /// @param to The address to which the tokens will be transferred
    /// @param amount The number of tokens to be transferred
    /// @return Returns true if the transfer was successful, otherwise false
    function forcedTransfer(
        address from,
        address to,
        uint256 amount
    ) external onlyRole(AGENT_ROLE) returns (bool) {
        return _forcedTransfer(from, to, amount);
    }

    /// @notice Mints new tokens and assigns them to a specified address
    /// @param _to The address that will receive the minted tokens
    /// @param amount The number of tokens to be minted
    function mint(address _to, uint256 amount) external onlyRole(AGENT_ROLE) {
        _mint(_to, amount);
    }

    /// @notice Burns tokens from a specified address
    /// @param account The address from which the tokens will be burned
    /// @param amount The number of tokens to be burned
    function burn(
        address account,
        uint256 amount
    ) external onlyRole(AGENT_ROLE) {
        _burn(account, amount);
    }

    /// @notice Freezes or unfreezes a specified address
    /// @param account The address to be frozen or unfrozen
    /// @param freeze The boolean value indicating whether to freeze (true) or unfreeze (false) the account
    function setAddressFrozen(
        address account,
        bool freeze
    ) external onlyRole(AGENT_ROLE) {
        _frozen[account] = freeze;

        emit AddressFrozen(account, freeze, _msgSender());
    }

    /// @notice Freezes a specified amount of tokens in a specified account
    /// @param account The account in which the tokens will be frozen
    /// @param amount The amount of tokens to be frozen
    function freezePartialTokens(
        address account,
        uint256 amount
    ) external onlyRole(AGENT_ROLE) {
        _freezePartialTokens(account, amount);
    }

    /// @notice Unfreezes a specified amount of tokens in a specified account
    /// @param account The account from which the tokens will be unfrozen
    /// @param amount The amount of tokens to be unfrozen
    function unfreezePartialTokens(
        address account,
        uint256 amount
    ) external onlyRole(AGENT_ROLE) {
        _unfreezePartialTokens(account, amount);
    }

    /// @notice Sets the Identity Registry contract address
    /// @param newIdentityRegistry The address of the new Identity Registry contract
    function setIdentityRegistry(
        address newIdentityRegistry
    ) external onlyRole(OWNER_ROLE) {
        _identityRegistry = IIdentityRegistry(newIdentityRegistry);
        emit IdentityRegistryAdded(newIdentityRegistry);
    }

    /// @notice Sets the Compliance contract address
    /// @param newCompliance The address of the new Compliance contract
    function setCompliance(
        address newCompliance
    ) external onlyRole(OWNER_ROLE) {
        require(newCompliance != address(0), "ERC-3643: Invalid zero address");

        _compliance.unbindToken(address(this));
        _compliance = ICompliance(newCompliance);
        _compliance.bindToken(address(this));
        emit ComplianceAdded(newCompliance);
    }

    /// @dev Returns the name of the token.
    function name() external view returns (string memory) {
        return _name;
    }

    /// @dev Returns the symbol of the token.
    function symbol() external view returns (string memory) {
        return _symbol;
    }

    /// @dev Returns the number of decimals the token uses.
    function decimals() external view returns (uint8) {
        return _decimals;
    }

    /// @dev Returns the onchainID of the token.
    function onchainID() external view returns (address) {
        return _onchainID;
    }

    /// @notice Get the balance of a specified account.
    /// @param account The address of the account.
    /// @return uint256 The balance of the specified account.
    function balanceOf(address account) external view returns (uint256) {
        return _balances[account];
    }

    /// @dev Returns the total supply of the token.
    function totalSupply() external view returns (uint256) {
        return _totalSupply;
    }

    /// @dev Returns the allowance of tokens that an owner has allowed a spender to spend.
    /// @param owner The address of the owner.
    /// @param spender The address of the spender.
    function allowance(
        address owner,
        address spender
    ) external view returns (uint256) {
        return _allowances[owner][spender];
    }

    /// @dev Returns whether an address is frozen.
    /// @param account The address to check.
    function isFrozen(address account) external view returns (bool) {
        return _frozen[account];
    }

    /// @dev Returns the number of frozen tokens of an address.
    /// @param account The address to check.
    function getFrozenTokens(address account) external view returns (uint256) {
        return _frozenAmounts[account];
    }

    /// @dev Returns the current Compliance contract linked to the token.
    function compliance() external view returns (address) {
        return address(_compliance);
    }

    /// @dev Returns the current Identity Registry contract linked to the token.
    function identityRegistry() external view returns (IIdentityRegistry) {
        return _identityRegistry;
    }

    /// @dev Returns the version of the token.
    function version() external pure returns (string memory) {
        return _TOKEN_VERSION;
    }

    /// @notice ERC-20 overridden function that include logic to check for trade validity.
    /// Require that the `from` and `to` addresses are not frozen.
    /// Require that the `amount` should not exceed available balance .
    /// Require that the `to` address is a verified address
    /// @param from The address of the sender
    /// @param to The address of the receiver
    /// @param amount The number of tokens to transfer
    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC-3643: transfer from zero address");
        require(to != address(0), "ERC-3643: transfer to zero address");

        require(!_frozen[to] && !_frozen[from], "ERC-3643: Wallet frozen");
        uint256 fromBalance = _balances[from];

        require(fromBalance >= amount, "ERC-3643: amount exceeds balance");
        require(
            amount <= fromBalance - (_frozenAmounts[from]),
            "ERC-3643: Freezed balance"
        );

        require(
            _identityRegistry.isVerified(to),
            "ERC-3643: Unverified identity"
        );
        require(
            _compliance.canTransfer(from, to, amount),
            "ERC-3643: Compliance failure"
        );

        unchecked {
            _balances[from] = fromBalance - amount;
            // Overflow not possible: the sum of all balances is capped by totalSupply, and the sum is preserved by
            // decrementing then incrementing.
            _balances[to] += amount;
        }

        emit Transfer(from, to, amount);
        _compliance.transferred(_msgSender(), to, amount);
    }

    /// @dev Mints the amount of tokens to the `account`
    /// @param account The address of the receiver
    /// @param amount The number of tokens to mint
    function _mint(address account, uint256 amount) private {
        require(account != address(0), "ERC-3643: mint to zero address");
        require(
            _identityRegistry.isVerified(account),
            "ERC-3643: Unverified identity"
        );
        require(
            _compliance.canTransfer(address(0), account, amount),
            "ERC-3643: Compliance failure"
        );

        _totalSupply += amount;
        _balances[account] += amount;

        emit Transfer(address(0), account, amount);
        _compliance.created(account, amount);
    }

    /// @dev Burns the amount of tokens from the sender's account
    /// @param account The address of the sender
    /// @param amount The number of tokens to burn
    function _burn(address account, uint256 amount) private {
        require(account != address(0), "ERC-3643: burn from the zero address");

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC-3643: burn exceeds balance");

        uint256 freeBalance = accountBalance - _frozenAmounts[account];
        if (amount > freeBalance) {
            uint256 tokensToUnfreeze = amount - (freeBalance);
            _frozenAmounts[account] =
                _frozenAmounts[account] -
                (tokensToUnfreeze);
            emit TokensUnfrozen(account, tokensToUnfreeze);
        }
        unchecked {
            _balances[account] = accountBalance - amount;
            // Overflow not possible: amount <= accountBalance <= totalSupply.
            _totalSupply -= amount;
        }

        emit Transfer(account, address(0), amount);
        _compliance.destroyed(account, amount);
    }

    /// @notice Approve a specified amount for a spender.
    /// @dev private function that approves a specified amount for a spender.
    /// Emits an Approval event.
    /// @param owner The address of the owner.
    /// @param spender The address of the spender.
    /// @param amount The amount to approve.
    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC-3643: approve from zero address");
        require(spender != address(0), "ERC-3643: approve to zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /// @notice Forced transfer of a specified amount from one address to another.
    /// @dev Private function to transfer tokens from one address to another.
    /// Requires that the `from` address has enough balance. Adjusts frozen tokens if necessary.
    /// @param from The address to transfer from.
    /// @param to The address to transfer to.
    /// @param amount The amount to transfer.
    /// @return true if the forced transfer is successful.
    function _forcedTransfer(
        address from,
        address to,
        uint256 amount
    ) private returns (bool) {
        uint fromBalance = _balances[from];

        require(fromBalance >= amount, "ERC-3643: Sender low balance");
        uint256 freeBalance = fromBalance - (_frozenAmounts[from]);
        if (amount > freeBalance) {
            uint256 tokensToUnfreeze = amount - (freeBalance);
            _frozenAmounts[from] = _frozenAmounts[from] - (tokensToUnfreeze);
            emit TokensUnfrozen(from, tokensToUnfreeze);
        }
        _transfer(from, to, amount);
        return true;
    }

    /// @dev Decreases the allowance of the spender for the sender's tokens
    /// @param owner The address of the sender
    /// @param spender The address of the spender
    /// @param amount The number of tokens to decrease the allowance by
    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) private {
        uint256 currentAllowance = _allowances[owner][spender];

        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "ERC3643: Insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    /// @dev Freezes or unfreezes the account
    /// @param account The address of the account
    /// @param freeze The boolean value of whether to freeze or unfreeze the account
    function _setAddressFrozen(address account, bool freeze) private {
        _frozen[account] = freeze;

        emit AddressFrozen(account, freeze, _msgSender());
    }

    /// @dev Freezes the amount of tokens in the account
    /// @param account The address of the account
    /// @param amount The number of tokens to freeze
    function _freezePartialTokens(address account, uint256 amount) private {
        uint256 balance = _balances[account];
        require(
            balance >= _frozenAmounts[account] + amount,
            "Amount exceeds available balance"
        );
        _frozenAmounts[account] = _frozenAmounts[account] + (amount);
        emit TokensFrozen(account, amount);
    }

    /// @dev Unfreezes the amount of tokens in the account
    /// @param account The address of the account
    /// @param amount The number of tokens to unfreeze
    function _unfreezePartialTokens(address account, uint256 amount) private {
        require(
            _frozenAmounts[account] >= amount,
            "Amount should be less than or equal to frozen tokens"
        );
        unchecked {
            _frozenAmounts[account] = _frozenAmounts[account] - (amount);
        }
        emit TokensUnfrozen(account, amount);
    }
}

